import React from 'react';
import Navbar from './components/Navbar';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom'
import './App.css';
import './components/Pages/SignInStyle.css'
import Home from './components/Pages/Home';
import Courses from './components/Pages/Courses';
import SignIn from './components/Pages/SignIn';
import Services from './components/Pages/Services';
import StaffSignIn from './components/Pages/StaffSignIn';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogginActive: true
    };
  }

  componentDidMount() {
    //Add .right by default
    this.rightSide.classList.add("right");
  }

  changeState() {
    const { isLogginActive } = this.state;

    if (isLogginActive) {
      this.rightSide.classList.remove("right");
      this.rightSide.classList.add("left");
    } else {
      this.rightSide.classList.remove("left");
      this.rightSide.classList.add("right");
    }
    this.setState(prevState => ({ isLogginActive: !prevState.isLogginActive }));
  }

  render() {
    const { isLogginActive } = this.state;
    const current = isLogginActive ? "Staff Portal" : "Student Portal";
    const currentActive = isLogginActive ? "signin" : "staffsignin";
    return (
      <div className="App">
        <div className="SignIn">
          <div className="container" ref={ref => (this.container = ref)}>
            {isLogginActive && (
              <SignIn containerRef={ref => (this.current = ref)} />
            )}
            {!isLogginActive && (
              <StaffSignIn containerRef={ref => (this.current = ref)} />
            )}
          </div>
          <RightSide
            current={current}
            currentActive={currentActive}
            containerRef={ref => (this.rightSide = ref)}
            onClick={this.changeState.bind(this)}
          />
        </div>
      </div>
    );
  }
}

const RightSide = props => {
  return (
    <div
      className="right-side"
      ref={props.containerRef}
      onClick={props.onClick}
    >
      <div className="inner-container">
        <div className="text">{props.current}</div>
      </div>
    </div>
  );
};

function App1() {
  return (
     <>
      <Router>
        <Navbar />
        <Switch>
          <Route path='/' exact component={Home} />
          <Route path='/services' component={Services} />
          <Route path='/courses' component={Courses} />
          <Route path='/signin' component={SignIn} />
          <Route path='/staffsignin' component={StaffSignIn} />
        </Switch>
      </Router>
    </>

  );
}



export default App;
